       )

mapper.select:
SELECT COUNT(*)
FROM TB_008
WHERE APLCN_CRTFC_PROPER_NUM = #{cpn}

mapper.insert:
INSERT INTO TB_008 (
    APLCN_DTLS_PROPER_NUM, CRTFC_TYPE, ISSUE_AGENCY, CRTFC_NUMBER, ISSUE_DATE
)
VALUES (
           #{aplcn_dtls_proper_num}, #{crtfc_type}, #{issue_agency}, #{crtfc_number}, #{issue_date}
