							trial_fcltt_proper_num,
							court_proper_num,
							accept_etc
							)
		values(#{USER_PROPER_NUM},
				#{trial_fcltt_proper_num},
				#{court_proper_num},
				#{accept_etc}
				)

mapper.select:
SELECT tb14_2.accept_pause, tb14_2.user_proper_num, tb14_2.pause_status, tb14_2.accept_proper_num, tb14_2.pause_start_date, tb14_2.pause_end_date, tb14_2.pause_submit_date, tb14_2.accept_pause_etc, tb1.user_name, tb14.accept_act_yn FROM TB_014_2 tb14_2 JOIN TB_001 tb1 ON tb1.user_proper_num = tb14_2.user_proper_num JOIN TB_014 tb14 ON tb14.user_proper_num = tb1.user_proper_num WHERE pause_status = 'Y'
AND accept_act_yn = #{cri.searchAccept_act_yn}
AND user_name = #{cri.searchUser_name}

SELECT COUNT(*) FROM TB_014_2 tb14_2 JOIN TB_001 tb1 ON tb1.user_proper_num = tb14_2.user_proper_num JOIN TB_014 tb14 ON tb14.user_proper_num = tb1.user_proper_num WHERE pause_status = 'Y'
AND accept_act_yn = #{cri.searchAccept_act_yn}
AND user_name = #{cri.searchUser_name}
