mapper.insert:
    	insert into TB_014(USER_PROPER_NUM,
							trial_fcltt_proper_num,
							court_proper_num,
							accept_etc
							)
		values(#{USER_PROPER_NUM},
				#{trial_fcltt_proper_num},
				#{court_proper_num},
				#{accept_etc}
				)

mapper.select:
SELECT tb14.accept_date, tb14.accept_act_yn, tb14.accept_etc, tb11.court_name, tb10.trial_fcltt_description, tb1.user_name, tb14.accept_proper_num FROM TB_014 tb14 JOIN TB_011 tb11 ON tb11.court_proper_num = tb14.court_proper_num JOIN TB_010 tb10 ON tb14.trial_fcltt_proper_num = tb10.trial_fcltt_proper_num JOIN TB_001 tb1 ON tb14.user_proper_num = tb1.user_proper_num
court_region = #{cri.searchCourt}
and accept_act_yn = #{cri.searchAct}
and user_name = #{cri.searchContent}
