mapper.select:
SELECT tb14_2.accept_status, tb14_2.accept_proper_num, tb14_2.pause_start_date, tb14_2.pause_end_date, tb14_2.pause_submit_date, tb14_2.accept_pause_etc, tb1.user_name, tb14.accept_act_yn FROM TB_014 tb14 JOIN TB_001 tb1 ON tb1.user_proper_num = tb14.user_proper_num JOIN TB_014_2 tb14_2 ON tb14_2.accept_proper_num = tb14.accept_proper_num WHERE accept_status != 'N'
AND accept_act_yn = #{cri.searchAccept_act_yn}
AND user_name = #{cri.searchUser_name}
LIMIT #{cri.pageStart}, #{cri.amount}

SELECT COUNT(*) FROM TB_014_2 tb14_2 JOIN TB_014 tb14 ON tb14.accept_proper_num = tb14_2.accept_proper_num JOIN TB_001 tb1 ON tb1.user_proper_num = tb14.user_proper_num WHERE accept_status != 'N'
AND accept_act_yn = #{cri.searchAccept_act_yn}
AND user_name = #{cri.searchUser_name}
